package br.com.peladafc.apppeladafc.util;import com.google.gson.Gson;import br.com.peladafc.apppeladafc.model.Time;/** * Created by renanjunior on 7/22/16. */public class UtilJson {    public static String toJson(Object obj){        Gson gson = new Gson();        String json = gson.toJson(obj);        return json;    }    public static Object fromJson(String str_time, Class cl) {        Gson gson = new Gson();        Object obj = gson.fromJson(str_time, cl);        return obj;    }}