package br.com.peladafc.apppeladafc.ws;import retrofit2.Retrofit;import retrofit2.converter.gson.GsonConverterFactory;/** * Created by renanjunior on 7/22/16. */public class RestClient {/*    Retrofit retrofit = new Retrofit.Builder()            .baseUrl("https://api.github.com/")            .build();    GitHubService service = retrofit.create(GitHubService.class);*/    private static final String BASE_URL = "https://apppeladafc.herokuapp.com";    public static Object getInstance(Class cl){        Retrofit retrofit = new Retrofit.Builder()                .baseUrl(BASE_URL)                .addConverterFactory(GsonConverterFactory.create())                .build();        Object service = retrofit.create(cl);        return service;    }}