package br.com.peladafc.apppeladafc.util;import org.junit.Before;import org.junit.Test;import br.com.peladafc.apppeladafc.model.Time;import okhttp3.internal.Util;import static org.junit.Assert.*;/** * Created by renanjunior on 7/22/16. */public class UtilJsonTest {    Time time;    @Before    public void setUp() throws Exception {        time = new Time();        time.setId(2);        time.setNome("Botafogo");    }    @Test    public void testToJson() throws Exception {        String str_time = UtilJson.toJson(time);        assertNotNull(str_time);        System.out.print("O resultado é : " + str_time);    }    @Test    public void fromJson() throws Exception{        String str_time = UtilJson.toJson(time);        assertNotNull(str_time);        System.out.print("O resultado é : "+str_time);        Time t = (Time) UtilJson.fromJson(str_time, Time.class);        assertNotNull(t);        System.out.print("O Objeto é : " + t);    }}